import {Component, OnInit} from '@angular/core';
import {EquipoService} from "./services/equipo.service";
import {Equipo} from "./model/equipo.model";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  equipos : Equipo[] = []
  equipoCreado: Equipo

  constructor(private equipoService : EquipoService) {
    this.equipoCreado = new Equipo('', '', 0);
  }

  ngOnInit() {
    this.equipoService.getEquipos().subscribe((res) => {
      this.equipos = res
    })
  }

  onSubmit() {
    this.equipos.push(this.equipoCreado)
  }
}
<div class="equipo" *ngFor="let equipo of equipos">
  <h3>{{equipo.nombre}}</h3>
  <p><i>Ciudad: </i>{{ equipo.ciudad }}</p>
  <p><i>Copas internacionales: </i> {{equipo.copasInternacionales}}</p>
</div>

<!-- equipo-form.component.html -->
<form (ngSubmit)="onSubmit()">
  <div>
    <label for="nombre">Nombre del Equipo:</label>
    <input type="text" id="nombre" [(ngModel)]="equipoCreado.nombre" name="nombre" required>
  </div>

  <div>
    <label for="ciudad">Ciudad:</label>
    <input type="text" id="ciudad" [(ngModel)]="equipoCreado.ciudad" name="ciudad" required>
  </div>

  <div>
    <label for="copasInternacionales">Copas Internacionales:</label>
    <input type="number" id="copasInternacionales" [(ngModel)]="equipoCreado.copasInternacionales" name="copasInternacionales" required>
  </div>

  <button type="submit">Guardar</button>
</form>
